import React, {Component} from 'react';
import './App.css';



function User(props){
  return(
      <tr className="row">
          <td> {props.user.id} </td>
          <td> {props.user.firstName} </td>
          <td> {props.user.lastName} </td>
          <td> { props.user.dob.slice(0,10)} </td>
      </tr>
  )
}

function Month(props){
  return(
    <div className="montName">
      {props.month.name + '  ' + props.month.count }
    </div>
  )
}

function devideByMonthes(users){
  let monthes = [];

  const monthNames = ["January", "February", "March", "April", "May", "June",
  "July", "August", "September", "October", "November", "December" ];
  users.forEach(user => {
    let dateOfBirth = new Date(user.dob);
    if(monthNames[dateOfBirth.getMonth()] in monthes){
      addUserToMonth(monthes[monthNames[dateOfBirth.getMonth()]], user);
    }
    else{
      monthes[monthNames[dateOfBirth.getMonth()]] = {
        name: monthNames[dateOfBirth.getMonth()],
        count: 1,
        users: [user]
      }
    }
  });
  return monthes;
}

function addUserToMonth(month, user){
  month.count++;
  month.users.push(user);
  console.log("Month: " + month.name + "  Count: " + month.count + "  Users: " + month.users.length);
}

class App extends Component {
  state = {
      users: [], 
      monthes: {}
  }

  componentDidMount() {
      const url = 'https://yalantis-react-school.herokuapp.com/api/task0/users'

        fetch(url)
        .then(result => result.json())
        .then(result => {
          this.setState({
            users: result,
          })
          this.monthes = devideByMonthes(result);
        })
    }

    renderUsers(){
      return (this.state.users.map( user => {
        return(
            <User user={user} key={user.id} />
        )})
      )
    }

    renderMonthes(){
      return(this.state.monthes.map(month =>{
        return (
          <Month month={month} />
        )
      }))
    }

  render() {
      return(
        // <table>
        //     <thead>
        //       <tr>
        //         <th>ID</th>
        //         <th>firstName</th>
        //         <th>lastName</th>
        //         <th>dob</th>
        //       </tr>
        //     </thead>
        //     <tbody>
        //     { 
        //     this.renderUsers()
        //     }
        //     </tbody>

        // </table>
        <div>
          {
            this.renderMonthes()
          }
        </div>
      ) 
  }
}

export default App;
