import React, {Component} from 'react';
import './App.css';

function User(props){
  return(
    <li>
      { props.user.firstName + " " + props.user.lastName + " " + props.user.dob.slice(0,10)}
    </li>
  )
}

function Month(props){
  return(
    <div className="monthContainer">
      <div className="monthName">
        {props.month.name + '  ' + props.month.count }
      </div>
      <ul className="listOfUsers">

      </ul>
    </div>

  )
}

function devideByMonthes(users){
  let monthes = [];

  const monthNames = ["January", "February", "March", "April", "May", "June",
  "July", "August", "September", "October", "November", "December" ];
  users.forEach(user => {
    let e = false;
    let dateOfBirth = new Date(user.dob);
    for(let i = 0; i < monthes.length; i++ ){
      if(monthNames[dateOfBirth.getMonth()] === monthes[i].name){
        addUserToMonth(monthes[i], user);
        e = true;
        break;
      }
    }
    if(!e) {
      let newMonth = {
        name: monthNames[dateOfBirth.getMonth()],
        count: 1,
        users: [user]
      }
      monthes.push(newMonth); 
    }
  });
  return monthes;
}

function addUserToMonth(month, user){
  month.count++;
  month.users.push(user);
  console.log("Month: " + month.name + "  Count: " + month.count + "  Users: " + month.users.length);
}

class App extends Component {
  state = {
      users: [], 
      monthes: []
  }

  componentDidMount() {
      const url = 'https://yalantis-react-school.herokuapp.com/api/task0/users'

        fetch(url)
        .then(result => result.json())
        .then(result => {
          this.setState({
            users: result,
            monthes: devideByMonthes(result)
          });
        })
    }

    renderMonthes(){
      return(this.state.monthes.map(month =>{
        return (
          <Month month={month} key={month.name}/>
        )
      }))
    }

  render() {
      return(
        // <table>
        //     <thead>
        //       <tr>
        //         <th>ID</th>
        //         <th>firstName</th>
        //         <th>lastName</th>
        //         <th>dob</th>
        //       </tr>
        //     </thead>
        //     <tbody>
        //     { 
        //     this.renderUsers()
        //     }
        //     </tbody>

        // </table>
        <div>
          {
            this.renderMonthes()
          }
        </div>
     ) 
  }
}

export default App;
