class App extends React.Component {
    state = {
        users: []
    }

    componentDidMount() {
        const url = 'https://yalantis-react-school.herokuapp.com/api/task0/users'

        fetch(url)
          .then(result => result.json())
          .then(result => {
            this.setState({
              data: result,
            })
          })
      }

    render() {
        return(
            <table>
                <tr>
                    <th>ID</th>
                    <th>firstName</th>
                    <th>lastName</th>
                    <th>dob</th>
                </tr>
                { 
                this.state.users.map( user => {
                    return(
                        <User user={user}/>
                    )
                }
                )
                }
            </table>
        ) 
    }
}

ReactDOM.render(<App />, document.getElementById('root'));
