class App extends React.Component {
    state = {
        users: [{id: 1, firstName: 'John', lastName: 'Smith', dob:'02'},
        {id: 1, firstName: 'John', lastName: 'Smith', dob:'02'},
        {id: 1, firstName: 'John', lastName: 'Smith', dob:'02'}]
    }

    render() {
        return(
            <table>
                <tr>
                    <th>ID</th>
                    <th>firstName</th>
                    <th>lastName</th>
                    <th>dob</th>
                </tr>
                { 
                this.state.users.map( user => {
                    return(
                        <User />
                    )
                }
                )
                }
            </table>
        ) 
    }
}

ReactDOM.render(<App />, document.getElementById('root'));
