import './App.css';

function User(props){
   let month = Date.parse(props.user.dob);
  // this.setState({
  //   monthes
  // })
  return(
      <tr className="row">
          <td> {props.user.id} </td>
          <td> {props.user.firstName} </td>
          <td> {props.user.lastName} </td>
          <td> {props.user.dob} </td>
      </tr>
  )
}

class App extends Component {
  state = {
      users: [],
      monthes: []
  }

  componentDidMount() {
      const url = 'https://yalantis-react-school.herokuapp.com/api/task0/users'

        fetch(url)
        .then(result => result.json())
        .then(result => {
          this.setState({
            users: result,
          })
        })
    }

