    renderUsers(){
      return (this.state.users.map( user => {
        return(
            <User user={user} key={user.id} />
        )})
      )
    }

  render() {
      return(
        <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>firstName</th>
                <th>lastName</th>
                <th>dob</th>
              </tr>
            </thead>
            <tbody>
            { 
            this.renderUsers()
            }
            </tbody>

        </table>
      ) 
  }
}

export default App;
