import React, {Component} from 'react';
import './App.css';

let monthes = {};

  const monthNames = ["January", "February", "March", "April", "May", "June",
  "July", "August", "September", "October", "November", "December" ];

function User(props){
   let dateOfBirth = new Date(props.user.dob);
   console.log(dateOfBirth.getMonth());
    if(monthNames[dateOfBirth.getMonth()] in monthes){
      addUsertoMonth(monthes[monthNames[dateOfBirth.getMonth()]], props.user);
      console.log(monthes[monthNames[dateOfBirth.getMonth()]]);
    }
    else{
      monthes[monthNames[dateOfBirth.getMonth()]] = {
        name: dateOfBirth.getMonth(),
        count: 1,
        users: [props.user]
      }
      console.log(monthes[monthNames[dateOfBirth.getMonth()]]);
    }

  return(
      <tr className="row">
          <td> {props.user.id} </td>
          <td> {props.user.firstName} </td>
          <td> {props.user.lastName} </td>
          <td> { props.user.dob.slice(0,10)} </td>
      </tr>
  )
}

function addUsertoMonth(month, user){
  month.count++;
  month.users.push(user);
  console.log("Month: " + month.name + "Count: " + month.count + "Users: " + month.users.lenght)
  console.log(month.count)
}

class App extends Component {
  state = {
      users: []
  }

  componentDidMount() {
      const url = 'https://yalantis-react-school.herokuapp.com/api/task0/users'

        fetch(url)
        .then(result => result.json())
        .then(result => {
          this.setState({
            users: result,
          })
        })
    }

  // devideByMonthes(){
  //   this.state.users.map( user => {
  //     return(

  //     )
  // }

    renderUsers(){
      return (this.state.users.map( user => {
        return(
            <User user={user} key={user.id} />
        )})
      )
    }

  render() {
      return(
        <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>firstName</th>
                <th>lastName</th>
                <th>dob</th>
              </tr>
            </thead>
            <tbody>
            { 
            this.renderUsers()
            }
            </tbody>

        </table>
      ) 
  }
}

export default App;
